mapper.select:
select o.id,o.agt_phone agtPhone,o.order_no orderNo,o.down_order_no reqStreamId,o.up_order_no upOrderNo,o.charge_addr chargeAddr,o.charge_money chargeMoney,
date_format(o.apply_time,'%Y-%m-%d %T') applyTime,date_format(o.check_time,'%Y-%m-%d %T') checkTime,o.qr_code qrCode,o.state,o.exp2 payAccount,
o.remark,o.error_code errorCode,o.channel_price channelPrice
from lfs_order_charge o
where 1=1

	and o.order_no = #{orderNo}


	and o.agt_phone =#{agtPhone}


	and o.up_order_no = #{upOrderNo}


	and o.down_order_no = #{reqStreamId}


	and o.state = #{state}


	and o.apply_time  between #{startTime} and #{endTime} 


	and o.charge_addr = #{chargeAddr}

order by apply_time desc

select o.charge_addr accountNo,bbi.bank_account accountName,o.apply_time applyTime, bbi.bank_name bankName,bbi.bank_mark bankMark from lfs_order_charge o left join lfs_bank_info bbi on o.charge_addr = bbi.card_no and bbi.status = 0 where 1=1
and o.order_no = #{orderNo}
and o.down_order_no = #{reqStreamId}

select o.id,o.order_no orderNo,o.down_order_no reqStreamId,o.up_order_no upOrderNo,o.charge_money chargeMoney,
date_format(o.apply_time,'%Y-%m-%d %T') applyTime,o.qr_code qrCode,o.state
from lfs_order_charge o
where 1=1

	and o.order_no = #{orderNo}


	and o.down_order_no = #{reqStreamId}


	and o.agt_phone =#{agtPhone}

order by qr_apply_time desc

SELECT a.*, d.agt_name agtName, a.succTotal/a.sumCount successRate, a.succTotalPrice - a.succTotalOutMoney - a.succTotalInMoney profit, (a.succTotalOutMoney - a.succTotalInMoney)/a.succTotalInMoney profitRate FROM ( SELECT e.agt_phone agtPhone, e.charge_addr chargeAddr, count(1) sumCount, sum(case when e.state = 0 then (e.price - e.out_money) else 0 end) succTotalOutMoney, sum(case when e.state = 0 then e.channel_price else 0 end) succTotalInMoney, sum(case when e.state = 0 then e.price else 0 end) succTotalPrice, count(case when e.state = 0 then 1 else null end) succTotal, sum(case when e.state = 1 then e.out_money else 0 end) failTotalOutMoney, sum(case when e.state = 1 then e.channel_price else 0 end) failTotalInMoney, sum(case when e.state = 1 then e.price else 0 end) failTotalPrice, count(case when e.state = 1 then 1 else null end) failTotal FROM lfs_order_charge e,lfs_product p WHERE e.product_code = p.product_num
and apply_time between #{startTime} and #{endTime}
and p.product_type = #{productType}
and e.charge_addr = #{chargeAddr}
and e.agt_phone = #{agtPhone}
GROUP BY e.charge_addr,e.agt_phone,p.product_type ) a, lfs_agent d WHERE a.agtPhone = d.agt_phone
and d.agt_name like CONCAT('%', #{agtName},'%')

select sum(charge_money) from lfs_order_charge
where charge_addr = #{chargeAddr}

	and apply_time  between #{startTime} and #{endTime} 


	and state = #{state}
