mapper.select:
select id,group_num,group_name,group_state,update_time,create_time from lfs_productgroup


		and group_num = #{groupNum}


		and date_format(create_time,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


		and date_format(create_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


		and group_name =#{groupName}


		and group_state =#{groupState}


order by create_time desc

mapper.update:
update lfs_productgroup


		and group_name =#{groupName}


		and group_state =#{groupState}

	update_time = now()

where group_num = #{groupNum}
